Function.prototype.Apply = function(obj, arr) {
    obj = obj || window;
    obj._tempFunction = this;
    var rv;
    if (!arr) rv = obj._tempFunction();
    else {
        var args = [];
        for (var i = 0, len = arr.length; i < len; i++) args.push('arr[' + i + ']');
        rv = eval("obj._tempFunction(" + args + ")");
    }
    delete obj._temFunction;
    return rv;
}
