function parentQueryString(url){
    var arguments = {};
    var arr = url.split("?");
    if(arr.length <= 1){
        return arguments;
    }
    arr = arr[1].split("&");
    for(var i = 0, k = arr.length; i < k; i++){
        var a = arr[i].split("=");
        arguments[a[0]] = a[1];
    }
    return arguments;
}

