function urlToString(url, parames) {
    var str = url;
    if(parames.constructor === Object && parames != {}) {
        str += "?";
        var para;
        for (var i in parames) {
            para = i + "=" + parames[i] + "&"
            str += para;
        }
    }
    return str.slice(0, -1);
}
